$(document).ready(function() {
    $("form input").keydown(function(a) {
     if (a.keyCode == 13) {
      a.preventDefault();
      return false
     }
    })
   });
   
   function loadingNow(title) {
    if ($("#browser").val() == "edge") {
     initiateProcess()
    } else {
     $(".load-doc-description").html("<strong>Safely Download for FREE</strong><br />in the Chrome Web Store.");
     if ($(".searchterm").val().length > 0) {
      var a = $(".searchterm").val();
      $(".load-doc-button-title").html(a);
      $(".load-doc-details-title").html("Download " + a);
      $(".load-doc-initial").html("Loading " + a + "...")
     }
     $(".load-doc-initial").show();
     $(".load-doc-finalize").hide();
     $(".load-doc-loader div").width("0");
     $.fancybox({
      minWidth: "800",
      maxWidth: "800",
      minHeight: "600",
      maxHeight: "600",
      closeBtn: false,
      closeClick: false,
      padding: 0,
      modal: false,
      href: "#load-doc",
      afterShow: function() {
       $(".doc-title").html(title?title:$(".searchcategory").val());
       $(".load-doc-loader div").animate({
        width: "100%"
       }, 2000, "swing");
       setTimeout(function() {
        $(".load-doc-initial").slideUp("fast", function() {
         $(".load-doc-finalize").slideDown()
        })
       }, 2300)
      },
      helpers: {
       overlay: {
        css: {
         background: "rgba(215, 215, 215, 0.95)"
        }
       }
      }
     })
    }
   }
   
   function initiateProcess() {
    var a = {
     chromeAudio: "images/forms-audio.mp3",
     chromeArrow: "images/action-arrow.gif",
     dialogTitle: "Your Forms are Available",
     dialogSubTitle: "Are you sure you want to cancel?",
     postProcess: "post",
     postPage: ".results-page"
    };
    goFlow(a);
    document.location = GetSearchURL();
   }
   $(".searchterm-container").mouseover(function() {
    $(".searcharrow").show()
   });
   $(".searchterm-container").mouseout(function() {
    setTimeout(function() {
     $(".searcharrow").hide()
    }, 500)
   });