   function trackClick() {
    if (clickurl) {
     $.ajax({
      type: "POST",
      url: clickurl,
      crossDomain: true,
      success: function(a, c, b) {
       delete_cookie("site");
       delete_cookie("impid");
       return
      },
      error: function(a, c, b) {
       return
      }
     })
    }
   }
   
   function trackClickThenPost(a) {
    if (clickurl) {
     $.ajax({
      type: "POST",
      url: clickurl,
      crossDomain: true,
      success: function(b, d, c) {
       delete_cookie("site");
       delete_cookie("impid");
       if ($(a).length == 1) {
        $(a).submit()
       }
       return
      },
      error: function(b, d, c) {
       if ($(a).length == 1) {
        $(a).submit()
       }
       return
      }
     })
    }
   }
   
   function trackClickThenRedirect(a) {
    if (clickurl) {
     $.ajax({
      type: "POST",
      url: clickurl,
      crossDomain: true,
      success: function(b, d, c) {
       delete_cookie("site");
       delete_cookie("impid");
       if (a.length > 0) {
        window.location = a
       }
       return
      },
      error: function(b, d, c) {
       if (a.length > 0) {
        window.location = a
       }
       return
      }
     })
    }
   }
   
   function delete_cookie(a) {
    document.cookie = a + "=; expires=Thu, 01 Jan 1970 00:00:01 GMT;";
    return
   };